import React from 'react';
import axios from 'axios';
import '../css/frontend.css';

class ProductRep extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            numproduct:this.props.pnumofitem,
            success:"", 
            error:"",
            tots: this.props.ptotal,
            subtotal:0,
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }


    handleChange = (e, numproduct ) => {
        this.setState(
            { [e.target.name]: e.target.value})
    }

    stepDown = (e)=>{
        if(this.state.numproduct >= 0){
            e.preventDefault();
        }
        this.setState({ numproduct: this.state.numproduct - 1});
    }   

    stepUp =(e)=>{
        e.preventDefault();
        this.setState({ numproduct: this.state.numproduct + 1 });
        // this.setState({ subtotal: parseInt(this.state.numproduct) * parseInt(this.props.pprice)})   
    }

    deleteCartItem = (e) =>{
        e.preventDefault();
        axios.delete('http://localhost:4000/remove/product/'+this.props.pid, this.state.config)
        .then(response=> {
          console.log(response.data.successmsg)
               alert(response.data.successmsg);
               window.location.reload();
          })
