import React, { Component } from 'react';
import AdminHead from './adminheader';
import axios from 'axios';


class AddProducts extends Component{
    constructor(){
        super();
        this.state={
            name: "",
            price:"",
            category:"",
            description:"",
            image:"",
            config: {
                headers: { 
                    'content-type': 'multipart/form-data',
                    'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    handleChange = (e) => {
        this.setState(
            { [e.target.name]: e.target.value }
        )
    }

    handleImageChange = (e) => {
        this.setState({
          image: e.target.files[0]
        })
      };

      productADD = (e) =>{
          e.preventDefault();
          let productDetails = new FormData();
          productDetails.append('name', this.state.name);
          productDetails.append('price', this.state.price);
          productDetails.append('category', this.state.category);
          productDetails.append('description', this.state.description);
          productDetails.append('loggedinmail', localStorage.getItem('email'));
          productDetails.append('image', this.state.image, this.state.image.name);
          axios.post('http://localhost:4000/products/add', productDetails, this.state.config)
            .then(response=> {
            console.log(response.data.successmsg)
                alert(response.data.successmsg);
            })
            .catch(error=>{            
                console.log(error.response)

                })
        }

        render(){
            return(
                <div>
                    <AdminHead/>

            <div class="card">
                <h5 class="card-header info-color white-text text-center py-4">
                    <strong> Product Details</strong>
                </h5>


                <div className="card-body px-lg-5 pt-0">
                    <form className="text-center">
                        <div className="md-form mt-3">
                            <label htmlFor="name">Product Name</label>
                            <input type="text" id="name" name="name"
                            value={this.state.name} onChange={this.handleChange} 
                            className="form-control"/>
                        </div>         
                        <div className="md-form">
                            <label fohtmlForr="price">Price</label>
                            <input type="text" id="price" name="price" 
                              value={this.state.price} onChange={this.handleChange} className="form-control"/>
                        </div>
                        <div className="md-form">
                            <label htmlFor="category">Category</label>
                            <input type="text" id="category" name="category" 
                              value={this.state.category} onChange={this.handleChange} className="form-control"/>
                        </div>
                        <div class="md-form">
                            <label for="image">Image</label>
                            <input type="file" id="image"
                            name="image" onChange={this.handleImageChange} class="form-control"/>
                        </div>
                        <div class="md-form">
                            <label htmlFor="description">Description</label>
                            <textarea id="description" class="form-control md-textarea" 
                            name="description"rows="3"></textarea>   
                        </div>
                        <input type="submit" value="Add Product" onClick={this.productADD}
                         class="btn btn-outline-info btn-rounded btn-block z-depth-0 my-4 waves-effect"/>
                    </form>
                </div>
            </div>
            </div>
            );
        }
    }

export default AddProducts;
