import React, { Component } from 'react';
import AdminNav from './adminheader';
import Sidebar from './adminsidebar';
import axios from 'axios';
import AdminReqView from './adminnotifyloop';



class AdminDashBody extends Component{
  constructor(props) {
    super(props)
    this.state = {
        arrayresp: 0,
        request: [],
        userid:localStorage.getItem('id'),
        config: {
            headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
        }
    }
}

componentDidMount() {
  axios.get('http://localhost:4000/displayrequest/', this.state.config)
      .then((response) => {
          this.setState({
              request: response.data

          })
      });     
}

render(){
      const userAdminReq = this.state.request.map((notifilist,index)=>{
        return <AdminReqView key={notifilist._id} reqid={notifilist._id}
        pname={notifilist.productname} pprice={notifilist.productprice} 
        pqaun={notifilist.productquantity} puser={notifilist.userName}
        pextracmnt={notifilist.extracomment}
        padminres={notifilist.adminresponse} admincomment={notifilist.admincomment}
        />
    })
    return(
      <div className="container-scroller">
         <AdminNav/>
         <div className="container-fluid page-body-wrapper">   
            <Sidebar/>

              <footer className="footer">
                <div className="d-sm-flex justify-content-center justify-content-sm-between">
                  <span className="text-muted text-center text-sm-left d-block d-sm-inline-block">
                    Copyright Â© 2020 Grocery Store Shopping.</span>
                  <span className="float-none float-sm-right d-block mt-1 mt-sm-0 text-center">
                    Admin Panel | React Basics</span>
                </div>
              </footer>
            </div>
          </div>

    );
  }
}
export default AdminDashBody;
