import React from 'react'

class ProductRep extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            numproduct:0,
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    handleChange = (e) => {
        this.setState(
            { [e.target.name]: e.target.value }
        )
    }

    stepDown = ()=>{
        this.parentNode.querySelector('input[type=number]').stepDown();
    }

    stepUp =()=>{
        this.parentNode.querySelector('input[type=number]').stepDown();
    }



    render(){
        return(
            <div class="col-lg-4">
                <div class="testimonial-item mx-auto mb-5 mb-lg-0">
                    <img class="img-fluid rounded-circle mb-3" 
                        src={`http://localhost:4000/productuploads/${this.props.pimage}`}  alt={this.props.pname} />
                    <h5>{this.props.pname} | Nrs.{this.props.pprice}/kg</h5>
                    <p class="font-weight-light mb-0">{this.props.pdescription}</p>
                    <div className="font-weight-light mb-0">
                        <form>
                            <div class="def-number-input number-input safari_only">
                                    <button onClick={this.stepUp} class="minus"></button>
                                    <input type="number" class="quantity" min="0" name="quantity" value={this.state.numproduct} onChange={this.handleChange} />
                                    <button onClick={this.stepDown} class="plus"></button>
                            </div>
                            <input type="submit" className="btn btn-primary" value="Add to cart"/>
                        </form>
                    </div>

                </div>
          </div>
        )

    }
}

export default ProductRep;
