import React, { Component } from 'react';
import '../css/frontend.css';
import Organic from '../images/organic.svg';
import Saving from '../images/saving.svg';
import Delivery from '../images/homedeliver.svg';
import ProductList from './productdatadisp';
import axios from 'axios';

class GroceryItems extends Component{
    constructor(props) {
        super(props);
        this.state = {
            products: [],
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    } 

    productDispData() {
        axios.get('http://localhost:4000/displayproducts', this.state.config)
            .then((response) => {
                this.setState({
                    products: response.data
                })
            });
    }


    render(){
        const allproduct = this.state.products.map((prolist,index)=>{
            return <ProductList key={prolist._id} name={prolist.name} price={prolist.price}
            category={prolist.category} image={prolist.image} description={prolist.description}
            hide_id={prolist._id}/>

        })
        return(
            <section className="features-icons bg-light text-center bg-body">
               <div className="container">
                    <div className="row">
                        <div className="col-lg-4">
                        <div className="features-icons-item mx-auto mb-5 mb-lg-0 mb-lg-3">
                            <div className="features-icons-icon d-flex">
                            <img className="icon-layers m-auto text-primary" 
                            src={Organic} alt="organic brands"/>
                            </div>
                            <p className="mb-0 image-header">The best organic brands</p>
                            <p className="lead mb-0">Shop 6,000+ wholesome food curated just for members</p>
                        </div>
                        </div>
                        <div className="col-lg-4">
                        <div className="features-icons-item mx-auto mb-5 mb-lg-0 mb-lg-3">
                            <div className="features-icons-icon d-flex">
                            <img className="icon-layers m-auto text-primary" 
                            src={Saving} alt="organic brands"/>
                            </div>
                            <p className="mb-0 image-header">The best saving deals</p>
                            <p className="lead mb-0">Enjoy reasonable prices, samples, and deals every day</p>
                        </div>
                        </div>
                        <div className="col-lg-4">
                        <div className="features-icons-item mx-auto mb-0 mb-lg-3">
                            <div className="features-icons-icon d-flex">
                            <img className="icon-layers m-auto text-primary" 
                            src={Delivery} alt="organic brands"/>
                            </div>
                            <p className="mb-0 image-header">Healthy without the Hassle</p>
                            <p className="lead mb-0">Fast and Free delivery with Pay on delivery service </p>
                        </div>
                        </div>
                    </div>
            </div>
            </section>
        );
    }
}

export default GroceryItems;
