import React, { Component } from 'react';
import AdminHead from './adminheader';


class AddProducts extends Component{
    constructor(){
        super();
        this.state={
            name: "",
            price:"",
            category:"",
            description:"",
            image:""
        }
    }

    handleChange = (e) => {
        this.setState(
            { [e.target.name]: e.target.value }
        )
    }

    handleImageChange = (e) => {
        this.setState({
          image: e.target.files[0]
        })
      };


        render(){
            return(
                <div>
                    <AdminHead/>

            <div class="card">
                <h5 class="card-header info-color white-text text-center py-4">
                    <strong> Product Details</strong>
                </h5>


                <div class="card-body px-lg-5 pt-0">
                    <form class="text-center">
                        <div class="md-form mt-3">
                            <label for="name">Product Name</label>
                            <input type="text" id="name" name="name"
                            value={this.state.name} onChange={this.handleChange} 
                            class="form-control"/>
                        </div>         
                        <div class="md-form">
                            <label for="price">Price</label>
                            <input type="text" id="price" name="price" 
                              value={this.state.price} onChange={this.handleChange} class="form-control"/>
                        </div>
                        <div class="md-form">
                            <label for="category">Category</label>
                            <input type="text" id="category" name="category" 
                              value={this.state.category} onChange={this.handleChange} class="form-control"/>
                        </div>
                        <div class="md-form">
                            <label for="image">Image</label>
                            <input type="file" id="image"
                            name="image" onChange={this.handleImageChange} class="form-control"/>
                        </div>
                        <div class="md-form">
                            <label for="description">Description</label>
                            <textarea id="description" class="form-control md-textarea" 
                            name="description"rows="3"></textarea>   
                        </div>
                        <input type="button" value="Add Product" class="btn btn-outline-info btn-rounded btn-block z-depth-0 my-4 waves-effect"/>
                    </form>
                </div>
            </div>
            </div>
            );
        }
    }

export default AddProducts;
