import React, { Component } from 'react';
import AdminHead from '../adminheader';
import axios from 'axios';
import '../../../css/adminpanel.css';
import {NavLink} from 'react-router-dom';
import Adminsidebar from '../adminsidebar';
import Testimg from '../../../images/logo.png';
import ProductLoop from './loopproducts';

class ViewProducts extends Component{
    constructor(props) {
        super(props);
        this.state = {
            products: [],
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    } 
    componentDidMount() {
        axios.get('http://localhost:4000/displayproducts', this.state.config)
            .then((response) => {
                this.setState({
                    products: response.data
                })
            });
    }

        render(){
            const adminLoopProduct = this.state.products.map((prolist,index)=>{
                return <ProductLoop key={prolist._id} pname={prolist.name} pprice={prolist.price}
                pcategory={prolist.category} pimage={prolist.image} pdescription={prolist.description}/>

            })
            return(
                <div>
                    <AdminHead/>
            <div className="container-fluid page-body-wrapper">   
                <Adminsidebar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="row">
                            <div className="col-md-12 grid-margin">
                                <div className="d-flex justify-content-between align-items-center">
                                    <div>
                                        <h4 className="font-weight-bold mb-0">Add New Product</h4>
                                    </div>
                                    <div>
                                        <NavLink to="/addproducts" className="btn btn-primary btn-icon-text btn-rounded">
                                        Add New Product</NavLink>
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-12 grid-margin stretch-card">
                                <div className="card position-relative">
                                    <div className="card-body">
                                        <h4 className="card-title">Detailed Reports</h4> 
                                        <div className="table-responsive">
                                            <table className="table table-hover table-striped">
                                                <thead>
                                                    <tr>
                                                        <th>Product Image</th>
                                                        <th>Name</th>
                                                        <th>Price</th>
                                                        <th>Category</th>
                                                        <th>Description</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {adminLoopProduct}
                                                </tbody>
                                            </table>
                                        </div>        
                                    </div>
                                </div>
                            </div>
                        </div>
                    <footer className="footer">
                    <div className="d-sm-flex justify-content-center justify-content-sm-between">
                        <span className="text-muted text-center text-sm-left d-block d-sm-inline-block">
                            Copyright Â© 2020 Grocery Store Shopping.</span>
                        <span className="float-none float-sm-right d-block mt-1 mt-sm-0 text-center">
                            Admin Panel | React Basics</span>
                    </div>
                    </footer>
                </div>
            </div>
         </div>
        </div>

            );
        }
    }

export default ViewProducts;
