import React, { Component } from 'react';
import AdminHead from '../adminheader';
import DummyProfile from '../../../images/logo.png';
import '../../../css/adminpanel.css';
import {NavLink} from 'react-router-dom';
import Adminsidebar from '../adminsidebar';


class ProfileBody extends Component{
        constructor(props) {
            super(props);
            this.state = {
                config: {
                    headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
                }
            }
        } 

        render(){

            return(
                <div>
                <AdminHead/>
        <div className="container-fluid page-body-wrapper">   
            <Adminsidebar/>
            <div className="main-panel">
                <div className="content-wrapper">
                    <div className="row">
                        <div className="col-md-12 grid-margin">
                            <div className="d-flex justify-content-between align-items-center">
                                <div>
                                    <h4 className="font-weight-bold mb-0">Profile Details</h4>
                                </div>
                                <div>
                                    <NavLink to={"/admin/profileupdate/"+localStorage.getItem('id')} className="btn btn-primary btn-icon-text btn-rounded">
                                    Update Profile</NavLink>
                                </div>
                            </div>
                        </div>
                        <div className="col-md-9 grid-margin stretch-card alignment-middle">
                            <div className="card position-relative">
                                <div className="card-body">
                                    <h4 className="profile-title">Your Profile Details</h4>
                                    {/* content */}
                                    <div class="profiledetail row">
                                        <div class="col-sm-2 col-md-2 left-space">
                                            <img src={DummyProfile} alt="" class="round-circle" />
                                        </div>
                                        <div class="col-sm-4 col-md-6">
                                            <h4>{localStorage.getItem('name')}</h4>
                                            <small><cite title={this.state.address}>{localStorage.getItem('address')} <i class="glyphicon glyphicon-map-marker">
                                            </i></cite></small>
                                            <p>
                                            <i class="glyphicon glyphicon-envelope"></i>{localStorage.getItem('email')}
                                            <br />
                                            <i class="glyphicon glyphicon-globe"></i>{localStorage.getItem('contact')}
                                            <br />
                                            <i class="glyphicon glyphicon-gift"></i>Role : {(localStorage.getItem('loginrole')) ? "Admin" : "General"}</p>
                                        </div>
                                    </div>
                                    {/* contentend */}
                                </div>
                            </div>
                        </div>
                    </div>
                <footer className="footer">
                <div className="d-sm-flex justify-content-center justify-content-sm-between">
                    <span className="text-muted text-center text-sm-left d-block d-sm-inline-block">
                        Copyright © 2020 Grocery Store Shopping.</span>
                    <span className="float-none float-sm-right d-block mt-1 mt-sm-0 text-center">
                        Admin Panel | React Basics</span>
                </div>
                </footer>
            </div>
        </div>
     </div>
    </div>
            );
        }
    }

export default ProfileBody;
