import React, { Component } from 'react';
import '../css/frontend.css';
// import Organic from '../images/organic.svg';
// import Saving from '../images/saving.svg';
// import Delivery from '../images/homedeliver.svg';
import ProductList from './productdatadisp';
import axios from 'axios';

class GroceryItems extends Component{
    constructor(props) {
        super(props);
        this.state = {
            products: [],
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    } 

    productDispData() {
        axios.get('http://localhost:4000/displayproducts', this.state.config)
            .then((response) => {
                this.setState({
                    products: response.data
                })
            });
    }


    render(){
        const allproduct = this.state.products.map((prolist,index)=>{
            return <ProductList key={prolist._id} name={prolist.name} price={prolist.price}
            category={prolist.category} image={prolist.image} description={prolist.description}/>

        })
        return(
            <section className="features-icons bg-light text-center bg-body">
               <div className="container">
                    <div className="row">
                       {allproduct}
                    </div>
            </div>
            </section>
        );
    }
}

export default GroceryItems;
