import React, { Component } from 'react';
import '../css/frontend.css';
// import Organic from '../images/organic.svg';
// import Saving from '../images/saving.svg';
// import Delivery from '../images/homedeliver.svg';
import ProductList from './productdatadisp';
import axios from 'axios';

class GroceryItems extends Component{
    constructor(props) {
        super(props);
        this.state = {
            products: [],
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    } 

    componentDidMount() {
        axios.get('http://localhost:4000/displayproducts', this.state.config)
            .then((response) => {
                this.setState({
                    products: response.data
                })
            });
    }


    render(){
        const allproduct = this.state.products.map((prolist,index)=>{
            return <ProductList key={prolist._id} pname={prolist.name} pprice={prolist.price}
            pcategory={prolist.category} pimage={prolist.image} pdescription={prolist.description}/>

        })
        return(
            <section class="testimonials text-center bg-light">
              <div class="container">
                <h2 class="mb-5">What people are saying...</h2>
                <div class="row">

                  <div class="col-lg-4">
                    <div class="testimonial-item mx-auto mb-5 mb-lg-0">
                      <img class="img-fluid rounded-circle mb-3" src="img/testimonials-2.jpg" alt="" />
                      <h5>Fred S.</h5>
                      <p class="font-weight-light mb-0">"Bootstrap is amazing. I've been using it to create lots of super nice landing pages."</p>
                    </div>
                  </div>
                  <div class="col-lg-4">
                    <div class="testimonial-item mx-auto mb-5 mb-lg-0">
                      <img class="img-fluid rounded-circle mb-3" src="img/testimonials-3.jpg" alt="" />
                      <h5>Sarah W.</h5>
                      <p class="font-weight-light mb-0">"Thanks so much for making these free resources available to us!"</p>
                    </div>
                  </div>
                </div>
              </div>
            </section>
        );
    }
}

export default GroceryItems;
