import React, { Component } from 'react';
import AdminHead from './adminheader';
import axios from 'axios';
import '../../css/adminpanel.css';
import Adminsidebar from './adminsidebar';
import {NavLink} from 'react-router-dom';

class AddProducts extends Component{
    constructor(){
        super();
        this.state={
            name: "",
            price:"",
            category:"",
            description:"",
            image:"",
            loggedinemail:localStorage.getItem('email'),
            config: {
                headers: { 
                    'content-type': 'multipart/form-data',
                    'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    handleChange = (e) => {
        this.setState(
            { [e.target.name]: e.target.value }
        )
    }

    handleImageChange = (e) => {
        this.setState({
          image: e.target.files[0]
        })
      };

      productADD = (e) =>{
          e.preventDefault();
          let productDetails = new FormData();
          productDetails.append('name', this.state.name);
          productDetails.append('price', this.state.price);
          productDetails.append('category', this.state.category);
          productDetails.append('description', this.state.description);
          productDetails.append('loggedinmail', this.state.loggedinemail);
          productDetails.append('image', this.state.image, this.state.image.name);
          axios.post('http://localhost:4000/products/add', productDetails, this.state.config)
            .then(response=> {
            console.log(response.data.successmsg)
                alert(response.data.successmsg);
            })
            .catch(error=>{            
                console.log(error.response)

                })
        }

        render(){
            return(
                <div>
                    <AdminHead/>
            <div className="container-fluid page-body-wrapper">   
                <Adminsidebar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="row">
                            <div className="col-md-12 grid-margin">
                                <div className="d-flex justify-content-between align-items-center">
                                    <div>
                                        <h4 className="font-weight-bold mb-0">Add New Product</h4>
                                    </div>
                                    <div>
                                        <NavLink to="/viewproducts" className="btn btn-primary btn-icon-text btn-rounded">Product List</NavLink>
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-12 grid-margin stretch-card">
                                <div className="card position-relative">
                                    <div className="card-body">
                                        <h4 className="card-title">Detailed Reports</h4> 
                                        <form className="forms-sample">
                                            <div className="form-group">
                                                <label htmlFor="name">Detailed Reports</label>
                                                <input type="text" className="form-control" name="name"
                                                value={this.state.name} onChange={this.handleChange}
                                                id="name" placeholder="Name" autoCapitalize autoFocus/>
                                            </div>    
                                            <div className="form-group">
                                                <label htmlFor="price">Product Price</label> 
                                                <input type="text" className="form-control" name="price"
                                                value={this.state.price} onChange={this.handleChange}
                                                id="price" placeholder="Product Price"/>
                                            </div>   
                                            <div className="form-group">
                                                <label htmlFor="category">Detailed Reports</label>
                                                <input type="text" className="form-control" name="category"
                                                value={this.state.category} onChange={this.handleChange}
                                                id="category" placeholder="Category" autoComplete autoCapitalize/>
                                            </div>   
                                            <div className="form-group">
                                                <label htmlFor="image">Product Image</label> 
                                                <input type="file" name="image" onChange={this.handleImageChange} 
                                                id="image" className="form-control"/>
                                            </div>
                                            <div className="form-group">
                                                <label htmlFor="description">Product Description</label> 
                                                <textarea className="form-control" id="description" name="description"
                                                onChange={this.handleChange}
                                                    rows="4"></textarea>
                                            </div>
                                            <input type="submit" onClick={this.productADD} className="btn btn-primary mr-2 float-left" />
                                            {/* <button className="btn btn-light float-left">Clear</button> */}
                                            </form>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    <footer className="footer">
                    <div className="d-sm-flex justify-content-center justify-content-sm-between">
                        <span className="text-muted text-center text-sm-left d-block d-sm-inline-block">
                            Copyright © 2020 Grocery Store Shopping.</span>
                        <span className="float-none float-sm-right d-block mt-1 mt-sm-0 text-center">
                            Admin Panel | React Basics</span>
                    </div>
                    </footer>
                </div>
            </div>
            </div> 
            );
        }
    }

export default AddProducts;
