import React, { Component } from 'react';
import '../../css/frontend.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

class AdminNotificationLoop extends Component{
    constructor(props) {
        super(props)
        this.state = {
            success:"", 
            error:"",
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    removeRequest = (e) =>{
        e.preventDefault();
        axios.delete('http://localhost:4000/remove/request/'+this.props.reqid, this.state.config)
        .then(response=> {
          console.log(response.data.successmsg)
            this.setState({
                success:response.data.successmsg
            });
            setTimeout(function() {
                window.location.reload();
            }, 2000);
        }).catch(error=>{            
            this.setState({
                error:"Request could not be canceled. Try Again."
            });
            console.log(error.response.data.errmsg)

            })
        }

        removeResponse = (e) =>{
            e.preventDefault();
            axios.delete('http://localhost:4000/remove/request/'+this.props.reqid, this.state.config)
            .then(response=> {
              console.log(response.data.successmsg)
                this.setState({
                    success:"Response has been Removed"
                });
                setTimeout(function() {
                    window.location.reload();
                }, 2000);
            }).catch(error=>{            
                this.setState({
                    error:"Response could not be removed."
                });
                console.log(error.response.data.errmsg)

                })
            }

    render(){
        return(
            <tbody>
            {this.props.padminres === false ?   
            <tr>
                <td>Jab</td>
                <td>Photoshop</td>
                <td className="text-danger"> 28.76% <i className="ti-arrow-down"></i></td>
                <td>Photoshop</td>
                <td><label className="badge badge-danger">Pending</label></td>
            </tr> 
            :    
            <tr>
                <td>Jacob</td>
                <td>Photoshop</td>
                <td className="text-danger"> 28.76% <i className="ti-arrow-down"></i></td>
                <td>Photoshop</td>
                <td><label className="badge badge-danger">Pending</label></td>
            </tr>    
               }

            {/* <div className="alert alert-info"> 
                <strong className="default">
                    <i className="fa fa-bell"></i> John
                </strong> sent you friend request. 
                <button type="button" className="close" data-dismiss="alert" aria-hidden="true">Ã—</button>
                {/* <button type="button" className="close" aria-hidden="true"><i class="fa fa-folder"></i></button> 
            </div> */}
        </tbody>         
        );
    }
}

export default AdminNotificationLoop;
