import React from 'react'
import axios from 'axios';

class AdminProductDisp extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            success:"",
            error:"",
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    deleteProduct = (e) =>{
        e.preventDefault();
        axios.delete('http://localhost:4000/products/'+this.props.pid, this.state.config)
        .then(response=> {
          console.log(response.data.successmsg)
          this.setState({
            success:response.data.successmsg
        });
          setTimeout(function() {
            window.location.reload()
           }, 3000);
          })
          .catch(error=>{            
            this.setState({
                error:"Something went wrong. Try again!"
              })
            console.log(error.response.data.errmsg)
            })
        }
    render(){
        return(
            <tbody>
                <tr>
                    <td colSpan="6" className="alert alert-info" style={{ display: this.state.success!=="" ? "" : "none" }}>
                        {this.state.success}
                    </td>
                    <td colSpan="6"  className="alert alert-danger" style={{ display: this.state.error!=="" ? "" : "none" }}>
                        {this.state.error}
                    </td>
                </tr>
                <tr>
                    <td className="py-1">
                        <img src={`http://localhost:4000/productuploads/${this.props.pimage}`}  
                        alt={this.props.pname} />
                    </td>
                    <td>{this.props.pname}</td>
                    <td>Nrs. {this.props.pprice}</td>
                    <td>{this.props.pcategory}</td>
                    <td>{this.props.pdescription}</td>
                    <td><button data-toggle="modal" data-target={"#exampleModalCenter"+this.props.pid} className="btn btn-inverse-info btn-fw">Update</button></td>
                    <td><button onClick={this.deleteProduct} className="btn btn-inverse-danger btn-fw">Remove</button></td>
                    <td className="modal fade" id={"exampleModalCenter"+this.props.pid} tabIndex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
                    <div className="modal-dialog modal-dialog-centered" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title first-cap" id="exampleModalLongTitle">Response for {this.props.puser}</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                        </div>
                        <div className="alert alert-info" style={{ display: this.state.success!=="" ? "" : "none" }}>
                                {this.state.success}
                        </div>
                        <div className="alert alert-danger" style={{ display: this.state.error!=="" ? "" : "none" }}>
                            {this.state.error}
                        </div> 
                        <div className="modal-body">
                            <form className="forms-sample">
                                <div className="form-group">
                                    <label htmlFor={this.props.pname} className="first-cap">Response Note</label>
                                    <textarea type="text" className="form-control"
                                    rows="3" column="3" onChange={this.handleChange}
                                    name="admincomment"
                                    value={this.state.admincomment} id={this.props.pname}>
                                    </textarea>
                                </div>
                                {/* <input type="submit" class="btn btn-primary mr-2 float-left"/> */}
                            </form>
                        </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                        <button type="submit" onClick={this.updateResponse} className="btn btn-primary">send Response</button>
                    </div>
                </div>
            </div>
        </td>
                </tr>
            </tbody>
        )

    }
}

export default AdminProductDisp;
