import React, { Component } from 'react';
import AdminNav from './adminheader';
import Sidebar from './adminsidebar';
import axios from 'axios';
import AdminReqView from './adminnotifyloop';



class AdminDashBody extends Component{
  constructor(props) {
    super(props)
    this.state = {
        arrayresp: 0,
        request: [],
        userid:localStorage.getItem('id'),
        config: {
            headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
        }
    }
}

componentDidMount() {
  axios.get('http://localhost:4000/displayrequest/', this.state.config)
      .then((response) => {
          this.setState({
              request: response.data

          })
      });     
}

render(){
      const userAdminReq = this.state.request.map((notifilist,index)=>{
        return <AdminReqView key={notifilist._id} reqid={notifilist._id}
        pname={notifilist.productname} pprice={notifilist.productprice} 
        pqaun={notifilist.productquantity} puser={notifilist.userName}
        padminres={notifilist.adminresponse} admincomment={notifilist.admincomment}
        />
    })
    return(
      <div>
         <AdminNav/>
         <div className="container-fluid page-body-wrapper">   
            <Sidebar/>
            <div className="main-panel">
              <div className="content-wrapper">
                <div className="row">
                  <div className="col-md-12 grid-margin">
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                          <h4 className="font-weight-bold mb-0">Dashboard Notification</h4>
                        </div>
                        {/* <div>
                          <a className="btn btn-primary btn-icon-text btn-rounded" href="/addproducts">
                            Add New Product
                          </a>
                        </div> */}
                      </div>
                    </div>
                  <div className="col-md-12 grid-margin stretch-card">
                    <div className="card position-relative">
                      <div className="card-body">
                          <h4 className="card-title">List of available Product</h4>
                        {/* content */}
                            <div className="alert alert-success"> 
                               <strong className="default first-cap">
                                  {/* <button type="button" className="close" data-dismiss="alert" aria-hidden="true">×</button> */}
                                  <i className="fa fa-user"></i> Welcome {localStorage.getItem("name")}
                                </strong><hr/>
                                <p>This is your admin Dashboard.
                              </p>
                         </div>

                        {/* endcontent */}
                      </div>
                    </div>
                  </div>
                </div>
              <footer className="footer">
                <div className="d-sm-flex justify-content-center justify-content-sm-between">
                  <span className="text-muted text-center text-sm-left d-block d-sm-inline-block">
                    Copyright © 2020 Grocery Store Shopping.</span>
                  <span className="float-none float-sm-right d-block mt-1 mt-sm-0 text-center">
                    Admin Panel | React Basics</span>
                </div>
              </footer>
            </div>
          </div>
         </div>
      </div>    
    );
  }
}
export default AdminDashBody;
