import React, { Component } from 'react';
import '../css/frontend.css';
import axios from 'axios';
import { NavLink } from 'react-router-dom';

class Notification extends Component{
    constructor(props) {
        super(props)
        this.state = {
            carts: [],
            userid:localStorage.getItem('id'),
            numproduct:0,
            total:0,
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    componentDidMount() {
        axios.get('http://localhost:4000/displaycart/'+this.state.userid, this.state.config)
            .then((response) => {
                this.setState({
                    carts: response.data
                })
            });
    }



    render(){
        return(
            <div className="testimonials text-center bg-light">
                <div className="col-md-6 col-lg-6 mx-auto mb-5 mb-lg-0">
                    <div className="panel panel-default">
                        <div className="panel-heading">
                            <h3 className="panel-title">Notifications</h3>
                        </div>
                        <div className="panel-body">
                            <div className="alert alert-success"> 
                            <strong className="default">
                                <i className="fa fa-road"></i> Well done,
                            </strong> You have finished job! 
                            <button type="button" className="close" data-dismiss="alert" aria-hidden="true">×</button>
                            </div>
                            <div className="alert alert-info"> 
                            <strong className="default">
                                <i className="fa fa-bell"></i> John
                            </strong> sent you friend request. 
                            <button type="button" className="close" data-dismiss="alert" aria-hidden="true">×</button>
                            {/* <button type="button" className="close" aria-hidden="true"><i class="fa fa-folder"></i></button> */}
                            </div>
                        </div>
                    </div>
                </div>
           </div>
        );
    }
}

export default Notification;
