import React, { Component } from 'react';
import '../../css/frontend.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

class AdminNotificationLoop extends Component{
    constructor(props) {
        super(props)
        this.state = {
            success:"", 
            error:"",
            admincomment:"",
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    handleChange = (e) => {
        this.setState(
            { [e.target.name]: e.target.value }
        )
    }

    removeRequest = (e) =>{
        e.preventDefault();
        axios.delete('http://localhost:4000/remove/request/'+this.props.reqid, this.state.config)
        .then(response=> {
          console.log(response.data.successmsg)
            this.setState({
                success:response.data.successmsg
            });
            setTimeout(function() {
                window.location.reload();
            }, 2000);
        }).catch(error=>{            
            this.setState({
                error:"Request could not be canceled. Try Again."
            });
            console.log(error.response.data.errmsg)

            })
        }

        removeResponse = (e) =>{
            e.preventDefault();
            axios.delete('http://localhost:4000/remove/request/'+this.props.reqid, this.state.config)
            .then(response=> {
              console.log(response.data.successmsg)
                this.setState({
                    success:"Response has been Removed"
                });
                setTimeout(function() {
                    window.location.reload();
                }, 2000);
            }).catch(error=>{            
                this.setState({
                    error:"Response could not be removed."
                });
                console.log(error.response.data.errmsg)

                })
            }

    render(){
        return(
            <tbody>
            {this.props.padminres === false ?   
            <tr>
                <td className="first-cap">{this.props.pname}</td>
                <td className="first-cap">{this.props.puser}</td>
                <td className="first-cap"> {this.props.pquan} <i className="ti-arrow-down"></i></td>
                <td>{this.props.pcomment}</td>
                <td><label className="badge badge-danger">Pending</label></td>
                <td><label className="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter">Respond</label></td>

                <div class="modal fade" id="exampleModalCenter" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
                <div class="modal-dialog modal-dialog-centered" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLongTitle">Modal title</h5>
                                <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                        </div>
                        <div class="modal-body">
                            <form class="forms-sample">
                                <div class="form-group">
                                    <label for="fullname">Name</label>
                                    <textarea type="text" class="form-control"
                                    rows="3" column="3" onChange={this.handleChange}
                                    name="admincomment"
                                    value={this.state.admincomment} id="admincomment" placeholder="fullname" value="Anmol koirala">
                                    </textarea>
                                </div>
                                <input type="submit" class="btn btn-primary mr-2 float-left"/>
                            </form>
                        </div>
                    <div class="modal-footer">
                        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                        <button type="button" class="btn btn-primary">Save changes</button>
                    </div>
                </div>
            </div>
        </div>
    </tr> 
            :    
            <tr>
                <td className="first-cap">{this.props.pname}</td>
                <td className="first-cap">{this.props.puser}</td>
                <td className="first-cap"> {this.props.pquan} <i className="ti-arrow-down"></i></td>
                <td>{this.props.pcomment}</td>
                <td><label className="badge badge-success">Response sent</label></td>
                <td><label className="btn btn-danger">Remove</label></td>
            </tr>    
               }


        </tbody>         
        );
    }
}

export default AdminNotificationLoop;
