import React from 'react'

class ProductRep extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            catmsg:"",
            numproduct:0,
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    handleChange = (e) => {
        this.setState(
            { [e.target.name]: e.target.value }
        )
    }

    stepDown = ()=>{
        this.setState({ numproduct: this.state.numproduct - 1 });
    }

    stepUp =(e)=>{
        e.preventDefault();
        this.setState({ numproduct: this.state.numproduct + 1 });
    }

    categoryCondition(){
        this.props.pcategory !== "vegetables" || "fruits"  ? this.setState(this.state.catmsg= "Vt"): this.setState(this.state.catmsg= "Vr");
    }



    render(){
        return(
            <div className="col-lg-4">
                <div className="testimonial-item mx-auto mb-5 mb-lg-0">
                    <img className="img-fluid rounded-circle mb-3" 
                        src={`http://localhost:4000/productuploads/${this.props.pimage}`}  alt={this.props.pname} />
    <h5>{this.props.pname} | Nrs.{this.props.pprice} {this.categoryCondition} </h5>
                    <p className="font-weight-light mb-0">{this.props.pdescription}</p>
                    <div className="font-weight-light mb-0">
                        <form>
                            <div className="def-number-input number-input safari_only">
                                <button onClick={this.stepDown}className="minus"></button>
                                    <input type="number" className="quantity" min="0" name="quantity" value={this.state.numproduct} onChange={this.handleChange} />
                                <button onClick={this.stepUp} className="plus"></button>
                            </div>
                            <input type="submit" className="btn btn-primary btn-cart" value="Add to cart"/>
                        </form>
                    </div>    
                </div>
          </div>
        )

    }
}

export default ProductRep;
