import React, { Component } from 'react';
import AdminHead from '../adminheader';
import axios from 'axios';
import '../../../css/adminpanel.css';
import Adminsidebar from '../adminsidebar';
import {NavLink} from 'react-router-dom';

class AdminProfileUpdate extends Component{
    constructor(){
        super();
        this.state={
            userid:localStorage.getItem('id'),
            fullname:localStorage.getItem('name'),
            email:localStorage.getItem('email'),
            contact:localStorage.getItem('contact'),
            address:localStorage.getItem('address'),
            config: {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    handleChange = (e) => {
        this.setState(
            { [e.target.name]: e.target.value }
        )
    }

    updateUser = () =>{
        const userdata = {
            fullname: this.state.fullname,
            contactnum: this.state.contactnum,
            address: this.state.address        
        };
        axios.post('http://localhost:4000/userprofile/update/'+this.state.userid, userdata, this.state.config)
        .then(response=> {
          console.log(response.data.successmsg)
               alert(response.data.successmsg);
          })
          .catch(error=>{            
            console.log(error.response)
                alert(error.response);
            })
        }


        render(){
            return(
                <div>
                    <AdminHead/>
            <div className="container-fluid page-body-wrapper">   
                <Adminsidebar/>
                <div className="main-panel">
                    <div className="content-wrapper">
                        <div className="row">
                            <div className="col-md-12 grid-margin">
                                <div className="d-flex justify-content-between align-items-center">
                                    <div>
                                        <h4 className="font-weight-bold mb-0">User Profile Update</h4>
                                    </div>
                                    <div>
                                        <NavLink to="/admin/profile" className="btn btn-primary btn-icon-text btn-rounded">Profile</NavLink>
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-12 grid-margin stretch-card">
                                <div className="card position-relative">
                                    <div className="card-body">
                                        <h4 className="card-title">Edit General Details here</h4> 
                                        <form className="forms-sample">
                                            <div className="form-group">
                                                <label htmlFor="fullname">Name</label>
                                                <input type="text" className="form-control" name="fullname"
                                                value={this.state.fullname} onChange={this.handleChange}
                                                id="fullname" placeholder="fullname" autoCapitalize="true" autoFocus/>
                                            </div> 
                                            <div className="form-group">
                                                <label htmlFor="contactnum">Contact</label>
                                                <input type="text" className="form-control" name="contactnum"
                                                value={this.state.contact} onChange={this.handleChange}
                                                id="contactnum" placeholder="Contact Num"/>
                                            </div>   
                                            <div className="form-group">
                                                <label htmlFor="address">Contact</label>
                                                <input type="text" className="form-control" name="address"
                                                value={this.state.address} onChange={this.handleChange}
                                                id="address" placeholder="Contact Num" autoCapitalize="true"/>
                                            </div>
                                            <input type="submit" onClick={this.updateUser} className="btn btn-primary mr-2 float-left" />
                                            {/* <button className="btn btn-light float-left">Clear</button> */}
                                            </form>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    <footer className="footer">
                    <div className="d-sm-flex justify-content-center justify-content-sm-between">
                        <span className="text-muted text-center text-sm-left d-block d-sm-inline-block">
                            Copyright Â© 2020 Grocery Store Shopping.</span>
                        <span className="float-none float-sm-right d-block mt-1 mt-sm-0 text-center">
                            Admin Panel | React Basics</span>
                    </div>
                    </footer>
                </div>
            </div>
            </div> 
            );
        }
    }

export default AdminProfileUpdate;
