import React, { Component } from 'react';
import {withRouter,NavLink} from 'react-router-dom';


class DashHeader extends Component{
    constructor(props) {
        super(props);
        this.state = {
            user: [],
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }    
logoutHandle = (e)=>{
  e.preventDefault();
  localStorage.removeItem('token');
  this.props.history.push('/login');
}

render(){
    return(
        <nav className="navbar navbar-light bg-light sticky-top">
             <div className="container">
                  <NavLink className="navbar-brand" to="/dashboard">Grocery Store Dashboard</NavLink>
                 <ul className="nav navbar-nav navbar-right">
                     <li><NavLink className="navbar-brand sub-navlink" to="/products">Products</NavLink></li>
                     <li><NavLink className="navbar-brand sub-navlink" to="/products">Make a Request</NavLink></li>
                     <li><NavLink className="navbar-brand sub-navlink" to="/user/cart">Your cart</NavLink></li>                  
                     <li><button className="btn btn-primary" type="submit" onClick={this.logoutHandle}>Logout</button></li>
                 </ul>
               </div>
           </nav>      
    );
  }
}
export default  withRouter(DashHeader);
