import React, { Component } from 'react';
import '../../css/frontend.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

class AdminNotificationLoop extends Component{
    constructor(props) {
        super(props)
        this.state = {
            success:"", 
            error:"",
            admincomment:"",
            adminresponse:"",
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    handleChange = (e) => {
        this.setState(
            { [e.target.name]: e.target.value }
        )
    }

    updateResponse = (e) =>{
        e.preventDefault();
        const responsedata = {
            admincomment: this.state.fullname,
            adminresponse: true 
        };
        axios.put('http://localhost:4000/userreq/update/'+this.props.match.params.id, responsedata, this.state.config)
        .then(response=> {
            this.setState({
                success:response.data.successmsg
            });
            setTimeout(function() {
                window.location.reload();
            }, 2000); 
          })
          .catch(error=>{    
            this.setState({
                error:"Could not update profile"
            });        
            console.log(error.response)
                alert(error.response);
            })
        }

        removeResponse = (e) =>{
            e.preventDefault();
            axios.delete('http://localhost:4000/remove/request/'+this.props.reqid, this.state.config)
            .then(response=> {
              console.log(response.data.successmsg)
                this.setState({
                    success:"Response has been Removed"
                });
                setTimeout(function() {
                    window.location.reload();
                }, 2000);
            }).catch(error=>{            
                this.setState({
                    error:"Response could not be removed."
                });
                console.log(error.response.data.errmsg)

                })
            }

    render(){
        return(
            <tbody>
            {this.props.padminres === false ?   
            <tr>
                <td className="first-cap">{this.props.pname}</td>
                <td className="first-cap">{this.props.puser}</td>
                <td className="first-cap"> {this.props.pquan} <i className="ti-arrow-down"></i></td>
                <td>{this.props.pcomment}</td>
                <td><label className="badge badge-danger">Pending</label></td>
                <td><label className="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter">Respond</label></td>

                <td className="modal fade" id="exampleModalCenter" tabIndex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLongTitle">Response for {this.props.puser}</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                        </div>
                        <div className="modal-body">
                            <form className="forms-sample">
                                <div className="form-group">
                                    <label htmlFor={this.props.pname} className="first-cap">Response Note</label>
                                    <textarea type="text" className="form-control"
                                    rows="3" column="3" onChange={this.handleChange}
                                    name="admincomment"
                                    value={this.state.admincomment} id={this.props.pname}>
                                    </textarea>
                                </div>
                                {/* <input type="submit" class="btn btn-primary mr-2 float-left"/> */}
                            </form>
                        </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                        <button type="button" className="btn btn-primary">Save changes</button>
                    </div>
                </div>
            </div>
        </td>
    </tr> 
            :    
            <tr>
                <td className="first-cap">{this.props.pname}</td>
                <td className="first-cap">{this.props.puser}</td>
                <td className="first-cap"> {this.props.pquan} <i className="ti-arrow-down"></i></td>
                <td>{this.props.pcomment}</td>
                <td><label className="badge badge-success">Response sent</label></td>
                <td><label className="btn btn-danger">Remove</label></td>
            </tr>    
               }


        </tbody>         
        );
    }
}

export default AdminNotificationLoop;
