import React, { Component } from 'react';
import '../css/frontend.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

class NotificationLoop extends Component{
    constructor(props) {
        super(props)
        this.state = {
            success:"", 
            error:"",
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    removeRequest = (e) =>{
        e.preventDefault();
        axios.delete('http://localhost:4000/request/'+this.props.reqid, this.state.config)
        .then(response=> {
          console.log(response.data.successmsg)
            this.setState({
                success:response.data.successmsg
            });
            setTimeout(function() {
                window.location.reload();
            }, 2000);
        }).catch(error=>{            
            this.setState({
                error:"Request could not be canceled. Try Again."
            });
            console.log(error.response.data.errmsg)

            })
        }

        removeResponse = (e) =>{
            e.preventDefault();
            axios.delete('http://localhost:4000/request/'+this.props.reqid, this.state.config)
            .then(response=> {
              console.log(response.data.successmsg)
                this.setState({
                    success:"Response has been Removed"
                });
                setTimeout(function() {
                    window.location.reload();
                }, 2000);
            }).catch(error=>{            
                this.setState({
                    error:"Response could not be removed."
                });
                console.log(error.response.data.errmsg)

                })
            }

    render(){
        return(
          <div>
            {this.props.padminres === false ?   
            <div>
                <div className="alert alert-info" style={{ display: this.state.success!=="" ? "" : "none" }}>
                    {this.state.success}
                </div>
                <div className="alert alert-danger" style={{ display: this.state.error!=="" ? "" : "none" }}>
                    {this.state.error}
                </div> 
                <div className="alert alert-info"> 
                    <strong className="default first-cap">
                        <i className="fa fa-smile-o"></i> Valued Consumer {this.props.puser}
                    </strong><hr/>
                    <p>Your Reuest for <strong>{this.props.pname}</strong> has been sent and is under
                    processing. We will respond soon. Thank you for your patience.</p> 
                    <p><label data-toggle="modal" data-target="#exampleModalCenter" className="btn alert-secondary first-cap">Update Request</label>></p>
                    <span><button type="submit" className="btn alert-danger first-cap">Cancel Request</button></span>
                </div>
                <div className="modal fade" id="exampleModalCenter" tabIndex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
                    <div className="modal-dialog modal-dialog-centered" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title first-cap" id="exampleModalLongTitle">Response for {this.props.puser}</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                            <div className="alert alert-info" style={{ display: this.state.success!=="" ? "" : "none" }}>
                                    {this.state.success}
                            </div>
                            <div className="alert alert-danger" style={{ display: this.state.error!=="" ? "" : "none" }}>
                                {this.state.error}
                            </div> 
                            <div className="modal-body">
                                <form className="forms-sample">
                                    <div className="form-group">
                                        <label htmlFor={this.props.pname} className="first-cap">Response Note</label>
                                        <textarea type="text" className="form-control"
                                        rows="3" column="3" onChange={this.handleChange}
                                        name="admincomment"
                                        value={this.state.admincomment} id={this.props.pname}>
                                        </textarea>
                                    </div>
                                    {/* <input type="submit" class="btn btn-primary mr-2 float-left"/> */}
                                </form>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                <button type="submit" onClick={this.updateResponse} className="btn btn-primary">send Response</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div> 
            :    
            <div>
                <div className="alert alert-info" style={{ display: this.state.success!=="" ? "" : "none" }}>
                    {this.state.success}
                </div>
                <div className="alert alert-danger" style={{ display: this.state.error!=="" ? "" : "none" }}>
                    {this.state.error}
                </div> 
                <div className="alert alert-success"> 
                     <strong className="default first-cap">
                        <button type="submit" onClick={this.removeResponse}className="close" aria-hidden="true">×</button>
                        <i className="fa fa-check"></i> Valued Consumer {this.props.puser}
                     </strong><hr/>
                     <p>
                         {
                         (this.props.admincomment === "") 
                         ?  this.props.admincomment 
                         : "Your Request for " + this.props.pname +" has been listed. We will try to add requested product within next few days. Thank you for your patience. "}

                        Meanwhile, you can <NavLink to="/products" className="notification-link"> browse store products </NavLink>
                        or send another
                        <NavLink to="/user/request" className="notification-link first-cap"> request </NavLink> if you like.
                    </p>
                </div>
            </div>

               }

            {/* <div className="alert alert-info"> 
                <strong className="default">
                    <i className="fa fa-bell"></i> John
                </strong> sent you friend request. 
                <button type="button" className="close" data-dismiss="alert" aria-hidden="true">×</button>
                {/* <button type="button" className="close" aria-hidden="true"><i class="fa fa-folder"></i></button> 
            </div> */}
        </div>          
        );
    }
}

export default NotificationLoop;
