import React from 'react'

class ProductRep extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }


    render(){
        return(
            <div className="col-lg-4 productlist">
                <div className="features-icons-item mx-auto mb-5 mb-lg-0 mb-lg-3">
                    <div className="features-icons-icon d-flex product-item">
                    <img class="img-fluid rounded-circle mb-3" 
                    src={`http://localhost:4000/productuploads/${this.props.pimage}`}  alt={this.props.pname} />
                    </div>
                    <p className="mb-0 image-header">{this.props.pname} | {this.props.pprice}</p>
                    <p className="lead mb-0">{this.props.pdescription}</p>
                </div>
            </div>
        )

    }
}

export default ProductRep;
