import React, { Component } from 'react';
import {withRouter,NavLink} from 'react-router-dom';
import  '../../css/adminheader.css';
import Logo from '../../images/logo.png';
import Logout from '../../images/logout.png';



class DashHeader extends Component{
    constructor(props) {
        super(props);
        this.state = {
            user: [],
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }    
    logoutHandle = (e)=>{
        e.preventDefault();
        localStorage.removeItem('token');
        this.props.history.push('/login');
    }

render(){
    return(
        // <nav className="navbar navbar-light bg-light sticky-top">
        //     <div className="container">
        //       <NavLink className="navbar-brand" to="/admindashboard">Grocery Admin Dashboard</NavLink>
        //         <ul className="nav navbar-nav navbar-right">
        //             <li><NavLink className="navbar-brand sub-navlink" to="/addproducts">Add Products</NavLink></li>
        //             {/* <li><NavLink className="navbar-brand sub-navlink" to="/register"></NavLink></li>                   */}
        //             <li><button className="btn btn-primary" type="submit" onClick={this.logoutHandle}>Logout</button></li>
        //         </ul>
        //       </div>
        //   </nav>   
        <nav className="navbar col-lg-12 col-12 p-0 fixed-top d-flex flex-row">
            <div className="text-center navbar-brand-wrapper d-flex align-items-center justify-content-center">
                <NavLink className="navbar-brand brand-logo mr-5" to="/admindashboard">
                    <span>Grocery Admin Panel</span>
                </NavLink>
            </div>
            <div className="navbar-menu-wrapper d-flex align-items-center justify-content-end">
        <ul className="navbar-nav navbar-nav-right">
          <li className="nav-item nav-profile dropdown">
            <a className="nav-link" onClick={this.logoutHandle} data-toggle="dropdown" id="profileDropdown">
              <img src={Logout}  alt="profile"/>
            </a>
          </li>
          <li className="nav-item nav-profile dropdown">
            <a className="nav-link" href="#" data-toggle="dropdown" id="profileDropdown">
              <img src={Logo} alt="profile"/>
            </a>
          </li>
        </ul>
        <button className="navbar-toggler navbar-toggler-right d-lg-none align-self-center" type="button" data-toggle="offcanvas">
          <span className="ti-view-list"></span>
        </button>
      </div>
    </nav>
    );
  }
}
export default  withRouter(DashHeader);
