import React, { Component } from 'react';
import '../css/frontend.css';

class CartBody extends Component{
    constructor(props) {
        super(props)
        this.state = {
            numproduct:0,
            config: {
                headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
        }
    }

    handleChange = (e) => {
        this.setState(
            { [e.target.name]: e.target.value }
        )
    }

    stepDown = ()=>{
        this.setState({ numproduct: this.state.numproduct - 1 });
    }

    stepUp =(e)=>{
        e.preventDefault();
        this.setState({ numproduct: this.state.numproduct + 1 });
    }

    componentDidMount() {
        axios.get('http://localhost:4000//displaycart/'+localStorage.getItem('id'), this.state.config)
            .then((response) => {
                this.setState({
                    carts: response.data
                })
            });
    }

    render(){
        return(
            <div className="cart-body">
                <div className="container mb-4">
                    <div className="row">
                        <div className="col-12">
                            <div className="table-responsive">
                                <table className="table table-striped table-bordered">
                                    <thead className="thead-dark">
                                        <tr>
                                            <th scope="col">Image</th>
                                            <th scope="col">Product</th>
                                            <th scope="col">Available</th>
                                            <th scope="col" className="text-center">Quantity</th>
                                            <th scope="col" className="text-center">Price</th>
                                            <th>Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr>
                                            <td>
                                                <img src="https://dummyimage.com/50x50/55595c/fff" /> 
                                            </td>
                                            <td>
                                                Product Name Dada
                                            </td>
                                            <td>
                                                In stock
                                            </td>
                                            <td>
                                                <div className="def-number-input number-input safari_only">
                                                    <button onClick={this.stepDown} className="minus"></button>
                                                        <input type="number" name="productnumber" className="quantity" min="0"
                                                        value={this.state.numproduct} onChange={this.handleChange} />
                                                    <button onClick={this.stepUp} className="plus"></button>
                                                </div>
                                            </td>
                                            <td className="text-center">
                                                124,90 €
                                            </td>
                                            <td className="text-center">
                                            <button className="btn btn-sm btn-danger">Remove</button> 
                                            </td>
                                        </tr>
                                        <tr>
                                            <td colSpan="4"></td>
                                            <td>Sub-Total</td>
                                            <td className="text-center">255,90 €</td>
                                        </tr>
                                        <tr>
                                            <td colSpan="4"></td>       
                                            <td>Shipping</td>
                                            <td className="text-center">6,90 €</td>
                                        </tr>
                                        <tr>
                                            <td colSpan="4"></td> 
                                            <td><strong>Total</strong></td>
                                            <td className="text-center"><strong>346,90 €</strong></td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                        <div className="col mb-2">
                            <div className="row">
                                <div className="col-sm-12  col-md-6">
                                    <button className="btn btn-lg btn-block btn-light text-uppercase">Continue Shopping</button>
                                </div>
                                <div className="col-sm-12 col-md-6 text-right">
                                    <button className="btn btn-lg btn-block btn-info text-uppercase">Checkout</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                {/* content */}
            </div>
        );
    }
}

export default CartBody;
